def clean_text(x):
	x = str(x)
	for punct in punctuation:
		x = x.replace(punct, f' {punct} ')
	re.sub(r'[^\x00-\x7f]',r'', x) 
	return x

def clean_numbers(x):
	x = re.sub('[0-9]{5,}', '10000', x)
	x = re.sub('[0-9]{4}', '1000', x)
	x = re.sub('[0-9]{3}', '100', x)
	x = re.sub('[0-9]{2}', '10', x)
	return x

