def replace_typical_misspell(text):
	mispellings = mispell_dict
	mispellings_re = re.compile('(%s)' % '|'.join(mispell_dict.keys()))
	def replace(match):
		return mispellings[match.group(0)]
	return mispellings_re.sub(replace, text)

def clean_text_column(train, test, text_col):
	print('===== Cleaning text column =====')
	train[text_col] = train[text_col].progress_apply(lambda x: x.lower())
	test[text_col] = test[text_col].progress_apply(lambda x: x.lower())

	# Clean the text
	train[text_col] = train[text_col].progress_apply(lambda x: clean_text(x))
	test[text_col] = test[text_col].progress_apply(lambda x: clean_text(x))
	
	# Clean numbers
	train[text_col] = train[text_col].progress_apply(lambda x: clean_numbers(x))
	test[text_col] = test[text_col].progress_apply(lambda x: clean_numbers(x))
	
	# Clean speelings
	train[text_col] = train[text_col].progress_apply(lambda x: replace_typical_misspell(x))
	test[text_col] = test[text_col].progress_apply(lambda x: replace_typical_misspell(x))
	
	## fill up the missing values
	train[text_col] = train[text_col].fillna("_##_").values
	test[text_col] = test[text_col].fillna("_##_").values
	print('===== Finished cleaning =====')
	return train, test
